// Class:   
//          PhotoRecord
//          Take photo and record info
// Constructor parameters:
//          db: The BrodySoft PhoneGap SQLlite plugin database reference or WebSQL database reference.

var PhotoRecord = function (db) {


    this.linguaDB = db;

    this.configDB = new ConfigDB(this.linguaDB);
    this.phoneGapUtils = new PhoneGapUtils();
    this.photoRecordData = new PhotoRecordData(this.linguaDB);
    this.descriptor = new Descriptor(this.linguaDB);

    //-------------------------------------------------------------------------------------------------------------

    // takePhoto
    //
    // Take the photo and if successful save it as a new photo record.
    this.takePhoto = function () {
        var deferred = $.Deferred();
        var self = this;

        if (typeof navigator.camera !== 'undefined') {
            // We can access the camera
            
            navigator.camera.getPicture(function (imageData) {
                // Photo succeeded, so need to save it!
                
                var promise = self.savePhoto(imageData);

                promise.done(function (data) {
                    // saving photo succeeded
                    deferred.resolve({ "unique_id": data.unique_id });
                });

                promise.fail(function () {
                    // saving photo failed
                    self.phoneGapUtils.showAlert("Saving the photo was not successful.");
                    deferred.reject();
                });
                
            },
            function () {
                // For some reason the photo failed.
                self.phoneGapUtils.showAlert("The photo was not successful.");
                deferred.reject();
            },
            {
                // Camera options
                quality: 90,
                destinationType: Camera.DestinationType.DATA_URL,
                sourceType: Camera.PictureSourceType.CAMERA,
                targetHeight: 1700,
                targetWidth: 1000,
                encodingType: Camera.EncodingType.JPEG,
                correctOrientation: true,
                saveToPhotoAlbum: false,
                cameraDirection: Camera.Direction.BACK,
                allowEdit:false
            });

        }
        else {
            if ($("#mobile").val() === "yes") {
                // We can't access the camera
                this.phoneGapUtils.showAlert("LinguaSnapp cannot access your camera.");
                deferred.reject();
            }
            else
            {
                // Just a test for when developing on a browser!
                var imageData = "";
                var promise = self.savePhoto(imageData);

                promise.done(function (data) {
                    // saving photo succeeded
                    deferred.resolve({ "unique_id": data.unique_id });
                });

                promise.fail(function () {
                    // saving photo failed
                    self.phoneGapUtils.showAlert("Saving the photo was not successful.");
                    deferred.reject();
                });
            }
        }

        return deferred.promise();
    }

    //-------------------------------------------------------------------------------------------------------------
    // savePhoto
    //
    // Initial save of the photo in the database (just a photo, dates and GPS). Returns the ID of the record.
    this.savePhoto = function(imageData) {
        var deferred = new $.Deferred();
        var lat = 999;
        var long = 999;
        var self = this;

        var gps_promise = this.phoneGapUtils.getGPS();

        gps_promise.done(function (data) {
            // If the GPS call was successful then use those coords
            lat = data.lat;
            long = data.long;
        });

        gps_promise.fail(function (data) {
            self.phoneGapUtils.showAlert("LinguaSnapp could not determine your location. Please enter the address in the Comments box on the photo's Context page.");
        });

        gps_promise.always(function () {
            // Regardless of whether the GPS call was successful go on to save the record anyway
            var promise = self.photoRecordData.saveNew(imageData, lat, long);

            promise.done(function (data) {
                // Saved
                deferred.resolve({ "unique_id": data.unique_id });
            });

            promise.fail(function () {
                // Not saved
                deferred.reject();
            });
        });

        return deferred.promise();
    }

   
    

    //-------------------------------------------------------------------------------------------------------------

    // savePhotoPage1
    //
    // Find the screen data and save it
    this.savePhotoPage1 = function () {
        var deferred = new $.Deferred();
        var self = this;

        
        // Grab any entered languages
        var languages = $(".vwLang").map(function () {
            return { "language": $(this).text(), "link_key": $(this).parent().attr("id").substr(4), "other": self.phoneGapUtils.htmlEscape($(this).attr("other_text")) };
        }).get();

        // Grab any entered alphabets
        var alphabets = $(".vwAlpha").map(function () {
            return { "alphabet": $(this).text(), "link_key": $(this).parent().attr("id").substr(4), "other": self.phoneGapUtils.htmlEscape($(this).attr("other_text")) };
        }).get();

        // Grab any entered translations
        var translations = $(".vwTrans").map(function () {
            return { "translation": self.phoneGapUtils.htmlEscape($(this).text()), "link_key": $(this).parent().attr("id").substr(4) };
        }).get();

        var input_data =
            {
                "title": self.phoneGapUtils.htmlEscape($("#txtTitle").val()),
                "numAlphabets": $("#numAlphabets").val(),
                "numLanguages": $("#numLanguages").val(),
                "languages": languages,
                "alphabets": alphabets,
                "translations": translations
            };

        var unique_id = $("#txtUniqueID1").text(); // Unique photo ID

        // Save
        var promise = this.photoRecordData.savePhotoRecord(unique_id, input_data);

        // Saved successfully
        promise.done(function () {
            deferred.resolve({
                "unique_id": unique_id
            });
        });

        // Save failed
        promise.fail(function () {
            deferred.reject();
        });

        return deferred.promise();
    }

    //-------------------------------------------------------------------------------------------------------------

    // savePhotoPage2
    //
    // Find the screen data and save it
    this.savePhotoPage2 = function () {
        var deferred = new $.Deferred();
        var self = this;

       
        var input_data =
            {
                "position": $("#ddlPosition").val(),
                "signType": $("#ddlSignType").val(),
                "outlet": $("#ddlOutlet").val(),
                "outletOther": self.phoneGapUtils.htmlEscape($("#txtOutletOther").val()),
                "contents": $("#ddlContent").map(function () {
                    return $(this).val();
                }).get(),
                "designs": $("#ddlDesign").map(function () {
                    return $(this).val();
                }).get(),
                "comments": self.phoneGapUtils.htmlEscape($("#txtComments").val())
            };

        var unique_id = $("#txtUniqueID2").text(); // Unique photo ID

        // Save
        var promise = this.photoRecordData.savePhotoRecord2(unique_id, input_data);

        // Saved successfully
        promise.done(function () {
            deferred.resolve({
                "unique_id": unique_id
            });
        });

        // Save failed
        promise.fail(function () {
            deferred.reject();
        });

        return deferred.promise();
    }

    //-------------------------------------------------------------------------------------------------------------

    // savePhotoPage3
    //
    // Find the screen data and save it
    this.savePhotoPage3 = function () {
        var deferred = new $.Deferred();
        var self = this;

        

        var oneLang = "no";

        if ($("#chkOneLang").is(":checked") === true) {
            oneLang = "yes";
        }

        var input_data =
            {
                "sector": $("#ddlSector").val(),
                "audience": $("#ddlAudience").val(),
                "oneLang":oneLang,
                "purposes": $("#ddlPurpose").map(function () {
                    return $(this).val();
                }).get(),
                "functions": $("#ddlFunction").map(function () {
                    return $(this).val();
                }).get(),
                "arrangements": $("#ddlArrangement").map(function () {
                    return $(this).val();
                }).get(),
                "dominances": $("#ddlDominance").map(function () {
                    return $(this).val();
                }).get(),
            };

        var unique_id = $("#txtUniqueID3").text(); // Unique photo ID

        // Save
        var promise = this.photoRecordData.savePhotoRecord3(unique_id, input_data);

        // Saved successfully
        promise.done(function () {
            deferred.resolve({
                "unique_id": unique_id
            });
        });

        // Save failed
        promise.fail(function () {
            deferred.reject();
        });

        return deferred.promise();
    }

    //-------------------------------------------------------------------------------------------------------------
    // deleteRecord()
    //
    // Delete the passed photo record
    this.deleteRecord = function (unique_id) {

        var deferred = new $.Deferred();
        var self = this;

        var promise = this.photoRecordData.deleteRecord(unique_id); // Delete the photo record
        promise.done(function () {
            deferred.resolve();
        });

        promise.fail(function () {
            deferred.reject();
        });

        return deferred.promise();
    }

    //-------------------------------------------------------------------------------------------------------------

    // getAllRecords
    //
    // Just return all the photo records - probably not really required but I wanted to try and not call data layer functions
    // from the presentation layer.
    this.getAllRecords = function () {
        var deferred = $.Deferred();
        var self = this;

        var promise = this.photoRecordData.getAllRecords();
        promise.done(function (data) {
            
            deferred.resolve(data);
        });

        promise.fail(function () {

            deferred.reject();
        });

        return deferred.promise();
    }

    //-------------------------------------------------------------------------------------------------------------

    // getRecord
    //
    // Just return all the photo record with the passed ID - probably not really required but I wanted to try and not call data layer functions
    // from the presentation layer.
    this.getRecord = function (unique_id) {
        var deferred = $.Deferred();
        var self = this;

        var promise = this.photoRecordData.getRecord(unique_id);
        promise.done(function (data) {
            deferred.resolve(data);
        });

        promise.fail(function () {

            deferred.reject();
        });

        return deferred.promise();
    }


    //-------------------------------------------------------------------------------------------------------------

    // createPayload
    //
    // Create the payload to transfer to the server.
    this.createPayload = function (unique_id) {
        var deferred = $.Deferred();
        var self = this;


        var ID_promise = self.configDB.getUserID(); // Get the user ID

        ID_promise.done(function (ID_data) { // Got the user ID successfully
            var promise = self.photoRecordData.getRecord(unique_id); // Get the photo record
            promise.done(function (data) { // Got the photo record
                $.extend(data, ID_data); // merge with the ID data and return.
                $.extend(data, { "eot": true });         

                deferred.resolve(data);
            });

            promise.fail(function () { // failed to get the photo record
                self.phoneGapUtils.showAlert("There was a problem retrieving the photo record. Upload failed.");
                deferred.reject();
            });
        });

        ID_promise.fail(function(){ // Unable to find user id
            self.phoneGapUtils.showAlert("There was a problem retrieving your user ID. Upload failed.");
            deferred.reject();
        });

        return deferred.promise();
    }
}